		return fmt.Errorf("command initialization timed out")
	case err := <-pc.waitForInitialization(stdout):
		if err != nil {
			return fmt.Errorf("command initialization failed: %w, stderr: %s", err, buf.String())
		}
	}

	return nil
}

func (pc *proxyCommand) waitForInitialization(stdout *io.ReadCloser) <-chan error {
	errChan := make(chan error)
	go func() {
		defer close(errChan)
		buffer := new(bytes.Buffer)
		_, err := io.Copy(buffer, stdout)
		if err != nil {
			errChan <- err
			return
		}
		if !bytes.Contains(buffer.Bytes(), []byte("initialized")) {
			errChan <- fmt.Errorf("child process did not initialize correctly")
		}
	}()
	return errChan
}

