	p.cmd.Stderr = os.Stderr

	if err := p.cmd.Start(); err != nil {
		return fmt.Errorf("failed to start proxy process: %v", err)
	}

	w.Close()

	scanner := bufio.NewScanner(r)
	timeout := time.After(5 * time.Second)
	for scanner.Scan() {
		line := strings.TrimSpace(scanner.Text())
		if line == "initialized" {
			return nil
		}
		select {
		case <-timeout:
			return errors.New("proxy initialization timed out")
		default:
		}
	}

	if err := scanner.Err(); err != nil {
		return fmt.Errorf("error reading from pipe: %v", err)
	}

	return errors.New("proxy did not initialize properly")
}
