package main

import (
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	"net/http"
)

type Conf struct {
	Port  int
	Debug bool
}

type App struct {
	Engine *echo.Echo
	Conf   *Conf
}

type AppOptions func(*App)

func WithConf(conf *Conf) AppOptions {
	return func(app *App) {
		app.Conf = conf
	}
}

func NewApp(opts ...AppOptions) *App {
	e := echo.New()
	app := &App{Engine: e}

	for _, opt := range opts {
		opt(app)
	}

	if app.Conf == nil {
		app.Conf = &Conf{Port: 8080, Debug: false}
	}

	e.Use(middleware.Recover())
	e.Use(middleware.Logger())

	e.GET("/", func(c echo.Context) error {
		return c.String(http.StatusOK, "Hello, World!")
	})

	return app
}

func (app *App) Run() {
	if err := app.Engine.Start(":" + string(app.Conf.Port)); err != nil {
		panic(err)
	}
}

