package main

import (
	"container/ring"
	"sync"
	"github.com/labstack/echo/v4"
	"github.com/go-playground/melody"
	"bridge/config"
)

type API struct {
	Messages  *ring.Ring
	RWMutex   sync.RWMutex
	Config    *config.Config
	mrouter   *melody.Melody
}

func New(cfg *config.Config) bridge.Bridger {
	api := &API{
		Messages: ring.New(cfg.MessageBufferSize),
		Config:   cfg,
		mrouter:  melody.New(),
	}

	e := echo.New()
	e.GET("/ws", api.handleWebsocket)
	e.GET("/health", api.handleHealthcheck)
	e.GET("/messages", api.handleMessages)
	e.GET("/stream", api.handleStream)
	e.POST("/message", api.handlePostMessage)

	go api.mrouter.HandleMessage(api.handleWebsocketMessage)

	go func() {
		if err := e.Start(cfg.ServerAddress); err != nil {
			panic(err)
		}
	}()

	return api
}

func (api *API) handleWebsocketMessage(message config.Message, s *melody.Session) {
	api.RWMutex.Lock()
	defer api.RWMutex.Unlock()

	api.Messages.Value = message
	api.Messages = api.Messages.Next()

	api.mrouter.Broadcast([]byte(message.String()))
}

func (api *API) handleWebsocket(c echo.Context) error {
	return api.mrouter.HandleRequest(c.Response(), c.Request())
}

func (api *API) handleHealthcheck(c echo.Context) error {
	return c.JSON(200, map[string]string{"status": "healthy"})
}

func (api *API) handleMessages(c echo.Context) error {
	api.RWMutex.RLock()
	defer api.RWMutex.RUnlock()

	var messages []config.Message
	for i := 0; i < api.Messages.Len(); i++ {
		messages = append(messages, api.Messages.Value.(config.Message))
		api.Messages = api.Messages.Next()
	}

	return c.JSON(200, messages)
}

func (api *API) handleStream(c echo.Context) error {
	conn, err := c.AcceptWebSocket()
	if err != nil {
		return err
	}
	defer conn.Close()

	for {
		messageType, msg, err := conn.ReadMessage()
		if err != nil {
			break
		}

		api.mrouter.Broadcast(msg)
		conn.WriteMessage(messageType, msg)
	}

	return nil
}

func (api *API) handlePostMessage(c echo.Context) error {
	var message config.Message
	if err := c.Bind(&message); err != nil {
		return err
	}

	api.handleWebsocketMessage(message, nil)
	return c.JSON(200, map[string]string{"status": "message posted"})
}
