package main

import (
	"github.com/labstack/echo/v4"
	"net/http"
)

type Host struct {
	Echo *echo.Echo
}

func setupProxyServer() {
	// Map to hold hostnames and their corresponding Echo instances
	hosts := map[string]*Host{
		"host1.example.com": {Echo: echo.New()},
		"host2.example.com": {Echo: echo.New()},
	}

	// Configure middleware, routes, etc., for each host
	for hostname, host := range hosts {
		host.Echo.Use(middleware.Logger())
		host.Echo.Use(middleware.Recover())

		// Example route
		host.Echo.GET("/", func(c echo.Context) error {
			return c.String(http.StatusOK, "Hello from "+hostname)
		})
	}

	// Create a new Echo instance for the proxy server
	e := echo.New()

	// Middleware to handle proxying
	e.Use(func(next echo.HandlerFunc) echo.HandlerFunc {
		return func(c echo.Context) error {
			req := c.Request()
			host := req.Host

			if h, ok := hosts[host]; ok {
				// Proxy the request to the appropriate host's Echo instance
				rec := &echo.ResponseRecorder{}
				c.SetHandler(h.Echo.Handler())
				h.Echo.ServeHTTP(rec, req)
				return c.Blob(rec.Status, rec.Header().Get(echo.HeaderContentType), rec.Body.Bytes())
			}

			// If no matching host, return 404
			return c.NoContent(http.StatusNotFound)
		}
	})

	// Start the proxy server
	e.Logger.Fatal(e.Start(":8080"))
}

func main() {
	setupProxyServer()
}
