		secretsHit = true
		return c.JSON(http.StatusOK, map[string]string{"message": "Secrets endpoint hit"})
	})

	e.POST("/trigger", func(c echo.Context) error {
		key := c.QueryParam("key")
		if key != builderKey {
			return c.JSON(http.StatusUnauthorized, map[string]string{"error": "Invalid key"})
		}
		triggerHit = true
		return c.JSON(http.StatusOK, map[string]string{"message": "Trigger endpoint hit"})
	})

	go func() {
		if err := e.Start(host + ":" + string(port)); err != nil && err != http.ErrServerClosed {
			e.Logger.Fatal(err)
		}
	}()

	select {}
}

