		cachedResponse, exists := cache.Get(c)
		if exists {
			c.JSON(http.StatusOK, gin.H{"message": cachedResponse})
			return
		}

		response := gin.H{"message": "pong", "timestamp": time.Now().Unix()}
		cache.Set(c, response, time.Minute)
		c.JSON(http.StatusOK, response)
	})

	r.Run(":8080")
}

