package main

import (
	"io"
	"net/http"
	"net/http/httputil"
	"net/url"

	"github.com/gin-gonic/gin"
)

func main() {
	r := gin.Default()

	r.GET("/proxy/:path", func(c *gin.Context) {
		// Load-balanced backend address
		backendURL, err := url.Parse("http://backend-server")
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to parse backend URL"})
			return
		}

		// Modify the request URL to point to the backend
		req, err := http.NewRequestWithContext(c.Request.Context(), c.Request.Method, backendURL.String()+"/"+c.Param("path"), c.Request.Body)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to create new request"})
			return
		}
		req.Header = c.Request.Header.Clone()

		// Use HTTP transport to forward the request
		transport := &http.Transport{}
		resp, err := transport.RoundTrip(req)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to forward request"})
			return
		}
		defer resp.Body.Close()

		// Copy backend response headers to client response
		for name, values := range resp.Header {
			for _, value := range values {
				c.Header(name, value)
			}
		}

		// Set status code from backend server
		c.Status(resp.StatusCode)

		// Stream the backend response body to the client
		_, err = io.Copy(c.Writer, resp.Body)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to stream response body"})
			return
		}
	})

	r.Run(":8080")
}
