type Options struct {
	ListenAddr string
}

type Server struct {
	counter int
	mu      sync.Mutex
}

func (s *Server) Run(op *Options) error {
	r := gin.Default()

	api := r.Group("/api/v1")
	{
		api.GET("/ping", s.ping)
		api.GET("/info", s.info)
		api.POST("/counter/increment", s.incrementCounter)
		api.POST("/counter/reset", s.resetCounter)
	}

	return r.Run(op.ListenAddr)
}

func (s *Server) ping(c *gin.Context) {
	c.JSON(http.StatusOK, gin.H{"message": "pong"})
	klog.Info("Ping request received")
}

func (s *Server) info(c *gin.Context) {
	c.JSON(http.StatusOK, gin.H{"status": "OK"})
	klog.Info("Info request received")
}

func (s *Server) incrementCounter(c *gin.Context) {
	s.mu.Lock()
	defer s.mu.Unlock()
	s.counter++
	c.JSON(http.StatusOK, gin.H{"counter": s.counter})
	klog.Infof("Counter incremented to %d", s.counter)
}

func (s *Server) resetCounter(c *gin.Context) {
	s.mu.Lock()
	defer s.mu.Unlock()
	s.counter = 0
	c.JSON(http.StatusOK, gin.H{"counter": s.counter})
	klog.Info("Counter reset to 0")
}

