func (r *request) HttpGet(queryUrl string, queryValues map[string]string, headerValues map[string]string) ([]byte, int, error) {
	urlWithQuery := r.QueryBuilder(queryUrl, queryValues)
	req, err := http.NewRequest("GET", urlWithQuery, nil)
	if err != nil {
		return nil, 0, err
	}

	for key, value := range headerValues {
		req.Header.Set(key, value)
	}

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, 0, err
	}
	defer resp.Body.Close()

	bodyBytes, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, 0, err
	}

	return bodyBytes, resp.StatusCode, nil
}

func (r *request) HttpPost(queryUrl string, queryValues map[string]string, headerValues map[string]string) ([]byte, int, error) {
	urlWithQuery := r.QueryBuilder(queryUrl, queryValues)
	jsonValue, err := json.Marshal(queryValues)
	if err != nil {
		return nil, 0, err
	}

	req, err := http.NewRequest("POST", urlWithQuery, bytes.NewBuffer(jsonValue))
	if err != nil {
		return nil, 0, err
	}

	for key, value := range headerValues {
		req.Header.Set(key, value)
	}
	req.Header.Set("Content-Type", "application/json")

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, 0, err
	}
	defer resp.Body.Close()

	bodyBytes, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, 0, err
	}

	return bodyBytes, resp.StatusCode, nil
}

func (r *request) QueryBuilder(queryUrl string, queryValues map[string]string) string {
	u, err := url.Parse(queryUrl)
	if err != nil {
		return queryUrl
	}

	q := u.Query()
	for key, value := range queryValues {
		q.Add(key, value)
	}
	u.RawQuery = q.Encode()

	return u.String()
}

