package main

import (
	"github.com/gin-gonic/gin"
	"time"
	"net/http"
	"fmt"
)

type RateLimiter struct {
	rate       float64
	bucketSize int
	tokens     float64
	lastRefill time.Time
}

func NewRateLimiter(rps int, burst int) *RateLimiter {
	return &RateLimiter{
		rate:       float64(rps),
		bucketSize: burst,
		tokens:     float64(burst),
		lastRefill: time.Now(),
	}
}

func (rl *RateLimiter) Allow() bool {
	now := time.Now()
	elapsed := now.Sub(rl.lastRefill).Seconds()
	rl.tokens += elapsed * rl.rate
	if rl.tokens > float64(rl.bucketSize) {
		rl.tokens = float64(rl.bucketSize)
	}
	rl.lastRefill = now

	if rl.tokens < 1 {
		return false
	}
	rl.tokens--
	return true
}

func rateLimitMiddleware(rl *RateLimiter) gin.HandlerFunc {
	return func(c *gin.Context) {
		if !rl.Allow() {
			c.JSON(http.StatusTooManyRequests, gin.H{"message": "too many requests"})
			c.Abort()
			return
		}
		c.Next()
	}
}

func ginRun(rps int) {
	r := gin.Default()

	rl := NewRateLimiter(rps, rps*2)
	r.Use(rateLimitMiddleware(rl))

	r.GET("/rate", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "request successful"})
	})

	fmt.Printf("Server started with rate limit: %d rps\n", rps)
	r.Run(":8080")
}

func main() {
	ginRun(5) // Example usage with 5 requests per second
}

