package main

import (
	"fmt"
	"net/http"
	"os"
	"path/filepath"
	"time"

	"github.com/gin-gonic/gin"
	"github.com/google/uuid"
)

func main() {
	r := gin.Default()

	// Serve static files from /root/file/
	r.Static("/files", "/root/file/")

	// File upload endpoint
	r.POST("/upload", func(c *gin.Context) {
		// Get the file from form data
		file, err := c.FormFile("file")
		if err != nil {
			c.JSON(http.StatusBadRequest, gin.H{"status": 1, "message": "Error retrieving the file"})
			return
		}

		// Generate a new filename
		ext := filepath.Ext(file.Filename)
		timestamp := time.Now().Format("20060102150405")
		newFilename := fmt.Sprintf("%s-%s%s", timestamp, uuid.New().String(), ext)

		// Save the file to the specified directory
		dst := filepath.Join("/root/file/", newFilename)
		if err := c.SaveUploadedFile(file, dst); err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"status": 1, "message": "Error saving the file"})
			return
		}

		// Return the file URL
		fileURL := fmt.Sprintf("http://localhost:8085/files/%s", newFilename)
		c.JSON(http.StatusOK, gin.H{"status": 0, "message": "File uploaded successfully", "data": gin.H{"url": fileURL}})
	})

	// Start the server
	if err := r.Run(":8085"); err != nil {
		fmt.Println("Failed to start server:", err)
		os.Exit(1)
	}
}

