package main

import (
	"bytes"
	"encoding/binary"
	"fmt"
	"net"
	"time"
)

type Config struct {
	KDCAddresses map[string][]string // Map of realms to KDC addresses
	Timeout      time.Duration       // Timeout for each connection attempt
}

type Client struct {
	Config *Config
}

func (c *Client) sendKDCTCP(realm string, b []byte) ([]byte, error) {
	kdcs := c.Config.KDCAddresses[realm]
	if len(kdcs) == 0 {
		return nil, fmt.Errorf("no KDC addresses found for realm %s", realm)
	}
	return c.dialSendTCP(kdcs, b)
}

func (c *Client) dialSendTCP(kdcs map[int]string, b []byte) ([]byte, error) {
	var err error
	for _, addr := range kdcs {
		conn, err := net.DialTimeout("tcp", addr, c.Config.Timeout)
		if err != nil {
			continue
		}
		defer conn.Close()
		return c.sendTCP(conn, b)
	}
	return nil, err
}

func (c *Client) sendTCP(conn *net.TCPConn, b []byte) ([]byte, error) {
	err := binary.Write(conn, binary.BigEndian, uint32(len(b)))
	if err != nil {
		return nil, err
	}
	_, err = conn.Write(b)
	if err != nil {
		return nil, err
	}

	var respLen uint32
	err = binary.Read(conn, binary.BigEndian, &respLen)
	if err != nil {
		return nil, err
	}

	resp := make([]byte, respLen)
