package dnsendpoint

import (
	"context"
	"encoding/binary"
	"net"
	"time"
)

type DNSEndpoint struct {
	Addr string
}

func (d *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (n int, err error) {
	conn, err := net.Dial("udp", d.Addr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if deadline, ok := ctx.Deadline(); ok {
		conn.SetDeadline(deadline)
	}

	transactionID := generateTransactionID()
	binary.BigEndian.PutUint16(payload, transactionID)

	_, err = conn.Write(payload)
	if err != nil {
		return 0, err
	}

	n, err = conn.Read(buf)
	if err != nil {
		return 0, err
	}

	responseTransactionID := binary.BigEndian.Uint16(buf)
	if responseTransactionID != transactionID {
		return 0, ErrInvalidTransactionID
	}

	return n, nil
}

func generateTransactionID() uint16 {
	return uint16(time.Now().UnixNano()) ^ 0x1234
}

var ErrInvalidTransactionID = errors.New("invalid transaction ID")

