	for _, addr := range kdcs {
		conn, err := net.DialTimeout("tcp", addr, c.Config.Timeout)
		if err != nil {
			continue
		}
		defer conn.Close()
		return c.sendTCP(conn, b)
	}
	return nil, fmt.Errorf("failed to connect to any KDC")
}

func (c *Client) sendTCP(conn *net.TCPConn, b []byte) ([]byte, error) {
	_, err := conn.Write(b)
	if err != nil {
		return nil, fmt.Errorf("failed to write to KDC: %v", err)
