	var length uint32
	err = binary.Read(conn, binary.BigEndian, &length)
	if err != nil {
		return nil, fmt.Errorf("failed to read response length: %v", err)
	}

	response := make([]byte, length)
	_, err = conn.Read(response)
	if err != nil {
