}

func (c *connections) Add(conn net.Conn) {
	c.m.Store(conn, struct{}{})
}

func (c *connections) Remove(conn net.Conn) {
	c.m.Delete(conn)
}

func main() {
	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

