		if err := conn.Handshake(); err != nil {
			return
		}
		c = conn
	}

	cmd, target, err := t.readHeader(c)
	if err != nil {
		return
	}

	switch cmd {
	case socks.CmdConnect:
		t.handleConnect(c, target)
	case socks.CmdUDPAssociate:
		t.handleUoT(c, target)
	default:
		return
	}
