func (t *Trojan) readHeader(r io.Reader) (cmd socks.Cmd, target socks.Addr, err error) {
	buf := make([]byte, 3)
	if _, err = io.ReadFull(r, buf); err != nil {
		return
	}
	if buf[0] != socks.Version5 {
		err = errors.New("unsupported SOCKS version")
		return
	}
	cmd = socks.Cmd(buf[1])
	if target, err = socks.ReadAddr(r); err != nil {
		return
	}
	return
}

