	n, err := conn.Read(buf)
	if err != nil {
		fmt.Println("Error reading from connection:", err)
		return
	}
	req, err := http.ReadRequest(bufio.NewReader(bytes.NewReader(buf[:n])))
	if err != nil {
		fmt.Println("Error parsing request:", err)
		return
	}
	pConn, err := h.proxy.Dial("tcp", req.Host)
	if err != nil {
		fmt.Println("Error dialing proxy:", err)
		return
	}
	defer pConn.Close()
	h.servRequest(req, pConn)
}

func (h *HTTP) servRequest(req *http.Request, pConn *proxy.Conn) {
	if h.user != "" && h.password != "" {
		auth := strings.SplitN(req.Header.Get("Proxy-Authorization"), " ", 2)
		if len(auth) != 2 || auth[0] != "Basic" || auth[1] != base64.StdEncoding.EncodeToString([]byte(h.user+":"+h.password)) {
			http.Error(pConn, "Unauthorized", http.StatusUnauthorized)
			return
		}
	}
	if req.Method == "CONNECT" {
		h.servHTTPS(req, pConn)
	} else {
		h.servHTTP(req, pConn)
	}
}

func (h *HTTP) servHTTPS(req *http.Request, conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 200 Connection Established\r\n\r\n"))
	tlsConn := tls.Server(conn, &tls.Config{InsecureSkipVerify: true})
	defer tlsConn.Close()
	clientConn, err := tls.Dial("tcp", req.Host, nil)
	if err != nil {
		fmt.Println("Error dialing TLS:", err)
		return
	}
	defer clientConn.Close()
	go io.Copy(tlsConn, clientConn)
	io.Copy(clientConn, tlsConn)
}

func (h *HTTP) servHTTP(req *http.Request, pConn *proxy.Conn) {
	if h.pretend {
		http.Error(pConn, "Not Found", http.StatusNotFound)
		return
	}
	req.URL.Scheme = "http"
	req.URL.Host = req.Host
	err := req.Write(pConn)
	if err != nil {
		fmt.Println("Error writing request:", err)
		return
	}
	resp, err := http.ReadResponse(bufio.NewReader(pConn), req)
	if err != nil {
		fmt.Println("Error reading response:", err)
		return
	}
	defer resp.Body.Close()
	resp.Write(pConn)
}
