package main

import (
	"context"
	"net"
	"sync"
	"time"
)

type Handler interface {
	ServeTCP(conn net.Conn)
}

type Server struct {
	Addr        string
	Net         string
	Handler     Handler
	ReadTimeout  time.Duration
	WriteTimeout time.Duration
	IdleTimeout  func() time.Duration
	lock        sync.RWMutex
	started     bool
	shutdown    chan struct{}
	conns       map[net.Conn]struct{}
}

func (s *Server) ListenAndServe() error {
	l, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	defer l.Close()

	s.lock.Lock()
	s.shutdown = make(chan struct{})
	s.conns = make(map[net.Conn]struct{})
	s.started = true
	s.lock.Unlock()

	var wg sync.WaitGroup
	for {
		conn, err := l.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}

		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}

	wg.Wait()
	return nil
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()

	s.lock.Lock()
	s.conns[conn] = struct{}{}
	s.lock.Unlock()

	conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
	conn.SetWriteDeadline(time.Now().Add(s.WriteTimeout))

	s.Handler.ServeTCP(conn)

	s.lock.Lock()
	delete(s.conns, conn)
	s.lock.Unlock()
}

func (s *Server) ShutdownContext(ctx context.Context) error {
	s.lock.Lock()
	if !s.isStarted() {
		s.lock.Unlock()
		return nil
	}
	close(s.shutdown)
	s.lock.Unlock()

	ticker := time.NewTicker(time.Second)
	defer ticker.Stop()

	for {
		select {
		case <-ticker.C:
			s.lock.Lock()
			if len(s.conns) == 0 {
				s.lock.Unlock()
				return nil
			}
			s.lock.Unlock()
		case <-ctx.Done():
			return ctx.Err()
		}
	}
}

func (s *Server) isStarted() bool {
	return s.started
}
