package main

import (
	"crypto/tls"
	"net"
	"sync"
	"time"
)

type Handler interface {
	Handle(conn net.Conn, msg []byte) error
}

type MsgAcceptFunc func(msg []byte) bool

type Server struct {
	Addr          string
	Net           string
	Listener      net.Listener
	TLSConfig     *tls.Config
	PacketConn    net.PacketConn
	Handler       Handler
	ReadTimeout   time.Duration
	WriteTimeout  time.Duration
	IdleTimeout   func() time.Duration
	MsgAcceptFunc MsgAcceptFunc
	lock          sync.RWMutex
	started       bool
	shutdown      chan struct{}
	conns         map[net.Conn]struct{}
	udpPool       sync.Pool
}

func (s *Server) ListenAndServe() error {
	s.init()
	listener, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	s.Listener = listener
	s.shutdown = make(chan struct{})
	go s.serveTCP(listener)
	return nil
}

func (s *Server) serveTCP(listener net.Listener) error {
	var wg sync.WaitGroup
	for {
		conn, err := listener.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}
		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()
	s.lock.Lock()
	s.conns[conn] = struct{}{}
	s.lock.Unlock()
	defer func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
	}()

	for {
		conn.SetReadDeadline(time.Now().Add(s.getReadTimeout()))
		buf := make([]byte, 1024)
		n, err := conn.Read(buf)
		if err != nil {
			return
		}
		msg := buf[:n]
		if s.MsgAcceptFunc != nil && !s.MsgAcceptFunc(msg) {
			continue
		}
		s.Handler.Handle(conn, msg)
	}
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}

func (s *Server) init() {
	s.lock.Lock()
	defer s.lock.Unlock()
	if s.conns == nil {
		s.conns = make(map[net.Conn]struct{})
	}
	s.started = true
}

func (s *Server) getReadTimeout() time.Duration {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.ReadTimeout
}
