	pb "path/to/your/proto" // Adjust the import path as necessary
)

var (
	flagAddress          = flag.String("address", "localhost:50051", "gRPC server address")
	flagTimeout          = flag.Int("timeout", 0, "connection timeout in seconds (0 for no timeout)")
	flagEnableProfiling  = flag.Bool("enable", false, "enable profiling")
	flagDisableProfiling = flag.Bool("disable", false, "disable profiling")
	flagRetrieveSnapshot = flag.Bool("retrieve", false, "retrieve a snapshot")
	flagSnapshot         = flag.String("snapshot", "", "snapshot identifier")
)

func remoteCommand() error {
	var opts []grpc.DialOption
	if *flagTimeout > 0 {
		opts = append(opts, grpc.WithBlock(), grpc.WithTimeout(time.Duration(*flagTimeout)*time.Second))
	} else {
		opts = append(opts, grpc.WithBlock())
	}

	conn, err := grpc.Dial(*flagAddress, opts...)
	if err != nil {
		return fmt.Errorf("failed to connect: %v", err)
	}
	defer conn.Close()

	client := pb.NewProfilerClient(conn)

	ctx := context.Background()

	if *flagEnableProfiling {
		_, err = client.EnableProfiling(ctx, &pb.Empty{})
		if err != nil {
			return fmt.Errorf("failed to enable profiling: %v", err)
		}
		fmt.Println("Profiling enabled.")
	}

	if *flagDisableProfiling {
		_, err = client.DisableProfiling(ctx, &pb.Empty{})
		if err != nil {
			return fmt.Errorf("failed to disable profiling: %v", err)
		}
		fmt.Println("Profiling disabled.")
	}

	if *flagRetrieveSnapshot {
		if *flagSnapshot == "" {
			return fmt.Errorf("snapshot identifier is required for retrieval")
		}
		response, err := client.RetrieveSnapshot(ctx, &pb.SnapshotRequest{Id: *flagSnapshot})
		if err != nil {
			return fmt.Errorf("failed to retrieve snapshot: %v", err)
		}
		fmt.Printf("Snapshot retrieved: %v\n", response)
	}

	return nil
}

