func downloadFile(ctx context.Context, unixSocketPath string, downloadRequest *server.DownRequest) (map[string]*server.DownResult, error) {
	conn, err := grpc.DialContext(ctx, "unix://"+unixSocketPath, grpc.WithTransportCredentials(insecure.NewCredentials()))
	if err != nil {
		return nil, fmt.Errorf("failed to connect to Dragonfly daemon: %w", err)
	}
	defer conn.Close()

	client := server.NewDfdaemonClient(conn)
	stream, err := client.Download(ctx, downloadRequest)
	if err != nil {
		return nil, fmt.Errorf("failed to initiate download: %w", err)
	}

	results := make(map[string]*server.DownResult)
	for {
		result, err := stream.Recv()
		if err != nil {
			return nil, fmt.Errorf("failed to receive download result: %w", err)
		}

		results[filepath.Join(downloadRequest.Output, result.Path)] = result

		if result.Finished {
			break
		}
	}

	return results, nil
}
