package main

import (
	"bytes"
	"context"
	"crypto/tls"
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

type SendWebhookSync struct {
	HttpMethod  string
	Url         string
	Body        string
	ContentType string
	User        string
	Password    string
	HttpHeader  map[string]string
}

func SendWebRequestSync(ctx context.Context, webhook *SendWebhookSync) error {
	// Create a custom transport with timeout and TLS settings
	transport := &http.Transport{
		TLSClientConfig: &tls.Config{InsecureSkipVerify: true},
		Timeout:       10 * time.Second,
	}

	// Create a new HTTP client with the custom transport
	client := &http.Client{
		Transport: transport,
	}

	// Prepare the request
	req, err := http.NewRequestWithContext(ctx, webhook.HttpMethod, webhook.Url, bytes.NewBuffer([]byte(webhook.Body)))
	if err != nil {
		return fmt.Errorf("failed to create request: %w", err)
	}

	// Set the content type header
	req.Header.Set("Content-Type", webhook.ContentType)

	// Add additional headers
	for key, value := range webhook.HttpHeader {
		req.Header.Set(key, value)
	}

	// Add basic auth if credentials are provided
	if webhook.User != "" && webhook.Password != "" {
		req.SetBasicAuth(webhook.User, webhook.Password)
	}

	// Send the request
	resp, err := client.Do(req)
	if err != nil {
		return fmt.Errorf("failed to send request: %w", err)
	}
	defer resp.Body.Close()

	// Read the response body
	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return fmt.Errorf("failed to read response body: %w", err)
	}

	// Check for success status code
	if resp.StatusCode >= 200 && resp.StatusCode < 300 {
		return nil
	}

	// Return error with response details
	return fmt.Errorf("request failed with status code %d: %s", resp.StatusCode, string(body))
}
