package main

import (
	"context"
	"flag"
	"fmt"
	"log"
	"math/rand"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/credentials/insecure"

	pb "path/to/routeguide"
)

var (
	tls                = flag.Bool("tls", false, "Connection uses TLS")
	serverHostOverride = flag.String("server_host_override", "x.test.youtube.com", "The server name used to verify the hostname returned by the TLS handshake")
	caFile             = flag.String("ca_file", "", "The file containing the CA root cert file")
	serverAddr         = flag.String("server_addr", "localhost:50051", "The server address in the format of host:port")
)

func main() {
	flag.Parse()

	var opts []grpc.DialOption
	if *tls {
		if *caFile == "" {
			log.Fatalf("CA file not provided")
		}
		creds, err := credentials.NewClientTLSFromFile(*caFile, *serverHostOverride)
		if err != nil {
			log.Fatalf("Failed to create TLS credentials %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithTransportCredentials(insecure.NewCredentials()))
	}

	conn, err := grpc.Dial(*serverAddr, opts...)
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()
	client := pb.NewRouteGuideClient(conn)

	printFeature(client, &pb.Point{Latitude: 409146138, Longitude: -746188906})
	printFeatures(client, &pb.Rectangle{
		Lo: &pb.Point{Latitude: 400000000, Longitude: -750000000},
		Hi: &pb.Point{Latitude: 420000000, Longitude: -730000000},
	})
	runRecordRoute(client)
	runRouteChat(client)
}

func printFeature(client pb.RouteGuideClient, point *pb.Point) {
	feature, err := client.GetFeature(context.Background(), point)
	if err != nil {
		log.Printf("client.GetFeature failed: %v", err)
		return
	}
	if feature.Name == "" {
		log.Printf("Found no feature at %v", point)
	} else {
		log.Printf("Found feature %v at %v", feature.Name, point)
	}
}

func printFeatures(client pb.RouteGuideClient, rectangle *pb.Rectangle) {
	call, err := client.ListFeatures(context.Background(), rectangle)
	if err != nil {
		log.Printf("client.ListFeatures failed: %v", err)
		return
	}
	for {
		feature, err := call.Recv()
		if err != nil {
			break
		}
		log.Printf("Found feature %v at %v", feature.Name, feature.Location)
	}
}

func runRecordRoute(client pb.RouteGuideClient) {
	points := []*pb.Point{
		{Latitude: 407838351, Longitude: -741298368},
		{Latitude: 408191800, Longitude: -740808400},
		{Latitude: 407838351, Longitude: -741298368},
	}
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()
	stream, err := client.RecordRoute(ctx)
	if err != nil {
		log.Printf("client.RecordRoute failed: %v", err)
		return
	}
	for _, point := range points {
		if err := stream.Send(point); err != nil {
			log.Printf("stream.Send failed: %v", err)
			return
		}
	}
	reply, err := stream.CloseAndRecv()
	if err != nil {
		log.Printf("stream.CloseAndRecv failed: %v", err)
		return
	}
	log.Printf("Finished trip with %d points", reply.PointCount)
	log.Printf("Passed %d features", reply.FeatureCount)
	log.Printf("Travelled %f meters", reply.Distance)
	log.Printf("It took %f seconds", reply.ElapsedTime.Seconds())
}

func runRouteChat(client pb.RouteGuideClient) {
	ctx, cancel := context.WithTimeout(context.Background(), time.Minute)
	defer cancel()
	stream, err := client.RouteChat(ctx)
	if err != nil {
		log.Printf("client.RouteChat failed: %v", err)
		return
	}
	names := []string{"Newark, NJ", "Kingsbridge, NY", "Chatham, NJ", "Hoboken, NJ"}
	for _, name := range names {
		if err := stream.Send(&pb.RouteNote{Name: name, Location: &pb.Point{Latitude: int32(rand.Intn(360*1e7)), Longitude: int32(rand.Intn(180*1e7))}}); err != nil {
			log.Printf("stream.Send failed: %v", err)
			return
		}
	}
	for {
		note, err := stream.Recv()
		if err != nil {
			break
		}
		log.Printf("Received message %q at point (%d, %d)", note.Name, note.Location.Latitude, note.Location.Longitude)
	}
}
