	pb "path/to/your/protobuf/package"
	"google.golang.org/grpc"
)

const (
	address = "localhost:1234"
)

func doClientWork() {
	conn, err := grpc.Dial(address, grpc.WithInsecure(), grpc.WithBlock())
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()
	c := pb.NewYourServiceClient(conn)

	// Unary RPC
	r, err := c.Hello(context.Background(), &pb.HelloRequest{Name: "World"})
	if err != nil {
		log.Fatalf("could not greet: %v", err)
	}
	fmt.Printf("Greeting: %s\n", r.GetMessage())

	// Streaming RPC
	stream, err := c.Channel(context.Background())
	if err != nil {
		log.Fatalf("could not open stream: %v", err)
	}

	// Send messages in a goroutine
	go func() {
		for i := 0; i < 10; i++ {
			msg := fmt.Sprintf("Message %d", i)
			if err := stream.Send(&pb.ChannelRequest{Message: msg}); err != nil {
				log.Fatalf("failed to send message: %v", err)
			}
			time.Sleep(time.Second)
		}
		if err := stream.CloseSend(); err != nil {
			log.Fatalf("failed to close send: %v", err)
		}
	}()

	// Receive messages in the main goroutine
	for {
		response, err := stream.Recv()
		if err != nil {
			log.Fatalf("failed to receive response: %v", err)
		}
		fmt.Printf("Received: %s\n", response.GetMessage())
	}
}

func main() {
	doClientWork()
}

