package main

import (
	"bytes"
	"context"
	"crypto/tls"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"

	"go.uber.org/zap"
)

type HTTPHeader struct {
	Key   string
	Value string
}

func httpProbe(protocol, address, path string, port int, headerList []*HTTPHeader, timeout time.Duration, responseSuccessFlag string, log *zap.SugaredLogger) (string, error) {
	url := fmt.Sprintf("%s://%s:%d%s", protocol, address, port, path)
	client := &http.Client{
		Timeout: timeout,
		Transport: &http.Transport{
			TLSClientConfig: &tls.Config{InsecureSkipVerify: true},
		},
	}

	req, err := http.NewRequestWithContext(context.Background(), "GET", url, nil)
	if err != nil {
		log.Errorf("Failed to create request: %v", err)
		return "Failure", err
	}

	for _, header := range headerList {
		req.Header.Add(header.Key, header.Value)
	}

	resp, err := client.Do(req)
	if err != nil {
		log.Errorf("Request failed: %v", err)
		return "Failure", err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		log.Errorf("Failed to read response body: %v", err)
		return "Failure", err
	}

	if resp.StatusCode != http.StatusOK {
		log.Errorf("Unexpected status code: %d", resp.StatusCode)
		return "Failure", fmt.Errorf("unexpected status code: %d", resp.StatusCode)
	}

	if responseSuccessFlag != "" && !bytes.Contains(body, []byte(responseSuccessFlag)) {
		log.Errorf("Response body does not contain success flag: %s", responseSuccessFlag)
		return "Failure", fmt.Errorf("response body does not contain success flag: %s", responseSuccessFlag)
	}

	log.Infof("Probe successful: %s", url)
	return "Success", nil
}
