type GRPCCollector struct {
	collpb.UnimplementedMetricsServiceServer
	headersMu sync.Mutex
	headers   metadata.MD
	storage   *Storage
	resultCh  <-chan ExportResult
	listener  net.Listener
	srv       *grpc.Server
}

func NewGRPCCollector(endpoint string, resultCh <-chan ExportResult) (*GRPCCollector, error) {
	lis, err := net.Listen("tcp", endpoint)
	if err != nil {
		return nil, err
	}
	srv := grpc.NewServer()
	collector := &GRPCCollector{
		storage:  NewStorage(),
		resultCh: resultCh,
		listener: lis,
		srv:      srv,
	}
	collpb.RegisterMetricsServiceServer(srv, collector)
	go func() {
		if err := srv.Serve(lis); err != nil {
			log.Fatalf("failed to serve: %v", err)
		}
	}()
	return collector, nil
}

func (c *GRPCCollector) Shutdown() {
	c.srv.GracefulStop()
}

func (c *GRPCCollector) Addr() net.Addr {
	return c.listener.Addr()
}

func (c *GRPCCollector) Collect() *Storage {
	return c.storage
}

func (c *GRPCCollector) Headers() map[string][]string {
	c.headersMu.Lock()
	defer c.headersMu.Unlock()
	return c.headers.Clone()
}

func (c *GRPCCollector) Export(ctx context.Context, req *collpb.ExportMetricsServiceRequest) (*collpb.ExportMetricsServiceResponse, error) {
	c.headersMu.Lock()
	c.headers, _ = metadata.FromIncomingContext(ctx)
	c.headersMu.Unlock()

	for _, resourceMetrics := range req.ResourceMetrics {
		for _, scopeMetrics := range resourceMetrics.ScopeMetrics {
			for _, metric := range scopeMetrics.Metrics {
				c.storage.AddMetric(metric)
			}
		}
	}

	select {
	case result := <-c.resultCh:
		if result.Error != nil {
			return nil, result.Error
		}
	default:
	}

	return &collpb.ExportMetricsServiceResponse{}, nil
}

type Storage struct {
	mu     sync.Mutex
	metrics []*metrics.Metric
}

func NewStorage() *Storage {
	return &Storage{}
}

func (s *Storage) AddMetric(metric *metrics.Metric) {
	s.mu.Lock()
	defer s.mu.Unlock()
	s.metrics = append(s.metrics, metric)
}

type ExportResult struct {
	Error error
}
