func validateConfig(proxyPort, spannerPort, cert string) error {
	if proxyPort == "" {
		return fmt.Errorf("proxyPort is required")
	}
	if spannerPort == "" {
		return fmt.Errorf("spannerPort is required")
	}
	if cert != "" {
		_, err := os.Stat(cert)
		if err != nil {
			return fmt.Errorf("certificate file %s does not exist", cert)
		}
	}
	return nil
}

func loadTLSCredentials(certFile string) (credentials.TransportCredentials, error) {
	cert, err := tls.LoadX509KeyPair(certFile, certFile)
	if err != nil {
		return nil, fmt.Errorf("failed to load key pair: %v", err)
	}

	certPool := x509.NewCertPool()
	pem, err := os.ReadFile(certFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read CA certificate: %v", err)
	}
	if ok := certPool.AppendCertsFromPEM(pem); !ok {
		return nil, fmt.Errorf("failed to append CA certificate")
	}

	return credentials.NewTLS(&tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientAuth:   tls.RequireAndVerifyClientCert,
		RootCAs:      certPool,
	}), nil
}

