}

func (c *completedGRPCServer) GracefulStop() {
	c.stopFunc()
}

func (c *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	listener, dial, netDial, err := c.listenerAndDialer()
	if err != nil {
		return nil, err
	}

	tlsOpts, certWatcher, err := c.tlsOpts()
	if err != nil {
		return nil, err
	}

	clientCreds, err := c.clientCreds()
	if err != nil {
		return nil, err
	}

	serverOpts := append([]grpc.ServerOption{
		grpc.Creds(clientCreds),
		grpc.KeepaliveParams(keepalive.ServerParameters{
			MaxConnectionAge: c.MaxConnAge,
		}),
	}, opts...)

	server := &completedGRPCServer{
		opts:            serverOpts,
		listener:        listener,
		svcRegistrationFn: svcRegistrationFn,
		listenFunc: func() error {
			srv := grpc.NewServer(serverOpts...)
			svcRegistrationFn(srv)
			return srv.Serve(listener)
		},
		prestopFunc: func() {},
		stopFunc: func() {
			listener.Close()
			if certWatcher != nil {
				certWatcher.Stop()
			}
		},
		dial: dial,
		netDial: netDial,
		creds: clientCreds,
		certWatcher: certWatcher,
	}

	return server, nil
}

func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error), func(context.Context, string) (net.Conn, error), error) {
	var listener net.Listener
	var dial func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	var netDial func(context.Context, string) (net.Conn, error)
	var err error

	switch c.Network {
	case "buffered":
		listener, dial, netDial, err = bufferedListener(c.Address, c.BufferSize)
	case "tcp":
		listener, err = net.Listen("tcp", c.Address)
		dial = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, c.Address, opts...)
		}
		netDial = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.DialContext(ctx, "tcp", addr)
		}
	case "unix":
		listener, err = net.Listen("unix", c.Address)
		dial = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, fmt.Sprintf("unix://%s", c.Address), opts...)
		}
		netDial = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.DialContext(ctx, "unix", addr)
		}
	default:
		return nil, nil, nil, fmt.Errorf("unsupported network type: %s", c.Network)
	}

	return listener, dial, netDial, err
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	config := &tls.Config{
		Certificates: []tls.Certificate{cert},
	}

	watcher, err := certwatcher.New(cert.Certificate[0])
	if err != nil {
		return nil, nil, err
	}

	go func() {
		for range watcher.Rotated() {
			newCert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
			if err != nil {
				continue
			}
			config.Certificates = []tls.Certificate{newCert}
		}
	}()

	return []grpc.ServerOption{grpc.Creds(credentials.NewTLS(config))}, watcher, nil
}

func (c *GRPCServerConfig) clientCreds() (credentials.TransportCredentials, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, err
	}

	certPool := x509.NewCertPool()
	if c.ClientCAPath != "" {
		pemData, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, err
		}
		if !certPool.AppendCertsFromPEM(pemData) {
			return nil, fmt.Errorf("failed to append client CA certificates")
		}
	}

	config := &tls.Config{
		Certificates: []tls.Certificate{cert},
		RootCAs:      certPool,
	}

	return credentials.NewTLS(config), nil
}
