package main

import (
	"crypto/tls"
	"fmt"
	"net"
	"strings"

	"google.golang.org/grpc"
	"google.golang.org/grpc/keepalive"
)

type Config struct {
	GRPCServer     *grpc.Server
	Listener       string
	Endpoint       string
	ServerTLSConfig *tls.Config
}

func createListener(config Config) (net.Listener, error) {
	network, addr := parseListener(config.Listener)
	if network == "" || addr == "" {
		return nil, fmt.Errorf("invalid listener address: %s", config.Listener)
	}

	listener, err := net.Listen(network, addr)
	if err != nil {
		return nil, fmt.Errorf("failed to listen: %v", err)
	}
	return listener, nil
}

func parseListener(listener string) (string, string) {
	if strings.HasPrefix(listener, "unix://") {
		return "unix", strings.TrimPrefix(listener, "unix://")
	} else if strings.HasPrefix(listener, "tcp://") {
		return "tcp", strings.TrimPrefix(listener, "tcp://")
	}
	return "", ""
}

func grpcServer(config Config) (*grpc.Server, error) {
	var opts []grpc.ServerOption

	if config.GRPCServer != nil {
		return config.GRPCServer, nil
	}

	if config.ServerTLSConfig != nil {
		opts = append(opts, grpc.Creds(credentials.NewTLS(config.ServerTLSConfig)))
	}

	// Add keepalive options
	opts = append(opts, grpc.KeepaliveParams(keepalive.ServerParameters{
		MaxConnectionIdle: 5 * time.Minute,
	}))

	server := grpc.NewServer(opts...)
	return server, nil
}
