package main

import (
	"context"
	"fmt"
	"github.com/apache/rocketmq-client-go/v2"
	"github.com/apache/rocketmq-client-go/v2/primitive"
	"github.com/apache/rocketmq-client-go/v2/producer"
)

type RocketMQProducer struct {
	p rocketmq.Producer
}

func initProducer() error {
	p, err := rocketmq.NewProducer(producer.WithNameServer([]string{"localhost:9876"}))
	if err != nil {
		return err
	}
	err = p.Start()
	if err != nil {
		return err
	}
	return nil
}

func sendOneWayMessage(ctx context.Context, topic string, body []byte) error {
	msg := &primitive.Message{
		Topic: topic,
		Body:  body,
	}
	return p.SendOneWay(ctx, msg)
}

func sendSyncMessage(ctx context.Context, topic string, body []byte) (*primitive.SendResult, error) {
	msg := &primitive.Message{
		Topic: topic,
		Body:  body,
	}
	return p.SendSync(ctx, msg)
}

func sendAsyncMessage(ctx context.Context, topic string, body []byte, callback func(ctx context.Context, result *primitive.SendResult, err error)) error {
	msg := &primitive.Message{
		Topic: topic,
		Body:  body,
	}
	return p.SendAsync(ctx, msg, callback)
}

func sendDelaySyncMessage(ctx context.Context, topic string, body []byte, tag string, keys []string, delayLevel int) (*primitive.SendResult, error) {
	msg := &primitive.Message{
		Topic: topic,
		Body:  body,
		Tags:  tag,
		Keys:  keys,
	}
	msg.ReconsumeTimes = delayLevel
	return p.SendSync(ctx, msg)
}

func main() {
	err := initProducer()
	if err != nil {
		fmt.Println("Failed to initialize producer:", err)
		return
	}
	defer p.Shutdown()

	ctx := context.Background()
	err = sendOneWayMessage(ctx, "test-topic", []byte("Hello, RocketMQ!"))
	if err != nil {
		fmt.Println("Error sending one-way message:", err)
	}

	result, err := sendSyncMessage(ctx, "test-topic", []byte("Hello, RocketMQ!"))
	if err != nil {
		fmt.Println("Error sending sync message:", err)
	} else {
		fmt.Println("Sync message sent successfully:", result)
	}

	err = sendAsyncMessage(ctx, "test-topic", []byte("Hello, RocketMQ!"), func(ctx context.Context, result *primitive.SendResult, err error) {
		if err != nil {
			fmt.Println("Error sending async message:", err)
		} else {
			fmt.Println("Async message sent successfully:", result)
		}
	})
	if err != nil {
		fmt.Println("Error initiating async message send:", err)
	}

	delayResult, err := sendDelaySyncMessage(ctx, "test-topic", []byte("Hello, RocketMQ!"), "tag1", []string{"key1"}, 3)
	if err != nil {
		fmt.Println("Error sending delayed sync message:", err)
	} else {
		fmt.Println("Delayed sync message sent successfully:", delayResult)
	}
}
