const cacheDir = "./cache"

func init() {
	if _, err := os.Stat(cacheDir); os.IsNotExist(err) {
		os.MkdirAll(cacheDir, 0755)
	}
}

func fetchFile(fileurl string, verbose bool) ([]byte, error) {
	cachePath := fmt.Sprintf("%s/%s", cacheDir, getCacheFilename(fileurl))

	if verbose {
		log.Printf("Checking cache for %s\n", fileurl)
	}

	if data, err := ioutil.ReadFile(cachePath); err == nil {
		if verbose {
			log.Printf("Cache hit for %s\n", fileurl)
		}
		return data, nil
	}

	if verbose {
		log.Printf("Cache miss for %s, fetching from URL\n", fileurl)
	}

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, fmt.Errorf("HTTP request failed with status: %s", resp.Status)
	}

	data, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if err := ioutil.WriteFile(cachePath, data, 0644); err != nil {
		return nil, err
	}

	return data, nil
}

func getCacheFilename(url string) string {
	// Simple hash function for demonstration purposes
	var sum int64
	for _, c := range url {
		sum += int64(c)
	}
	return fmt.Sprintf("%d", sum)
}
