package main

import (
	"bytes"
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

type WorkWeiXin struct {
	CorpID     string
	AgentID    string
	CorpSecret string
	Token      string
}

func (w *WorkWeiXin) getToken() (string, error) {
	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/gettoken?corpid=%s&corpsecret=%s", w.CorpID, w.CorpSecret)
	resp, err := http.Get(url)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	var tokenResp struct {
		AccessToken string `json:"access_token"`
		ExpiresIn   int    `json:"expires_in"`
		Errcode     int    `json:"errcode"`
		Errmsg      string `json:"errmsg"`
	}

	err = json.Unmarshal(body, &tokenResp)
	if err != nil {
		return "", err
	}

	if tokenResp.Errcode != 0 {
		return "", fmt.Errorf("failed to get token: %s", tokenResp.Errmsg)
	}

	w.Token = tokenResp.AccessToken
	return w.Token, nil
}

func (w *WorkWeiXin) Send(receivers []string, title string, content []byte, others ...string) error {
	if w.Token == "" {
		token, err := w.getToken()
		if err != nil {
			return err
		}
		w.Token = token
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/message/send?access_token=%s", w.Token)

	message := map[string]interface{}{
		"touser":  strings.Join(receivers, "|"),
		"msgtype": "markdown",
		"agentid": w.AgentID,
		"markdown": map[string]string{
			"content": fmt.Sprintf("# %s\n\n%s", title, content),
		},
	}

	if len(others) > 0 {
		for _, other := range others {
			key, value := parseKeyValue(other)
			if key != "" && value != "" {
				message[key] = value
			}
		}
	}

	jsonMessage, err := json.Marshal(message)
	if err != nil {
		return err
	}

	req, err := http.NewRequest("POST", url, bytes.NewBuffer(jsonMessage))
	if err != nil {
		return err
	}
	req.Header.Set("Content-Type", "application/json")

	client := &http.Client{Timeout: time.Duration(10 * time.Second)}
	resp, err := client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}

	var response struct {
		Errcode int    `json:"errcode"`
		Errmsg  string `json:"errmsg"`
	}

	err = json.Unmarshal(body, &response)
	if err != nil {
		return err
	}

	if response.Errcode != 0 {
		return fmt.Errorf("failed to send message: %s", response.Errmsg)
	}

	return nil
}

func parseKeyValue(s string) (string, string) {
	parts := strings.SplitN(s, "=", 2)
	if len(parts) != 2 {
		return "", ""
	}
