type WebsocketProxy struct {
	Director func(incoming *http.Request, out http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (p *WebsocketProxy) ServeHTTP(rw http.ResponseWriter, req *http.Request) {
	upgrader := websocket.Upgrader{}
	if p.Upgrader != nil {
		upgrader = *p.Upgrader
	}

	conn, err := upgrader.Upgrade(rw, req, nil)
	if err != nil {
		return
	}
	defer conn.Close()

	backendURL := p.Backend(req)
	if backendURL == nil {
		return
	}

	dialer := websocket.DefaultDialer
	if p.Dialer != nil {
		dialer = p.Dialer
	}

	backendConn, resp, err := dialer.Dial(backendURL.String(), nil)
	if err != nil {
		return
	}
	defer backendConn.Close()

	if p.Director != nil {
		p.Director(req, backendConn.Request().Header)
	}

	go func() {
		for {
			messageType, message, err := conn.ReadMessage()
			if err != nil {
				break
			}
			err = backendConn.WriteMessage(messageType, message)
			if err != nil {
				break
			}
		}
	}()

	for {
		messageType, message, err := backendConn.ReadMessage()
		if err != nil {
			break
		}
		err = conn.WriteMessage(messageType, message)
		if err != nil {
