package wol

import (
	"bytes"
	"encoding/hex"
	"net"
	"strings"
)

func WakeUp(macAddr, broadcastIP, bcastInterface string, port int) error {
	// Validate MAC address
	mac, err := net.ParseMAC(strings.ReplaceAll(macAddr, ":", ""))
	if err != nil {
		return err
	}

	// Create magic packet
	var magicPacket bytes.Buffer
	for i := 0; i < 6; i++ {
		magicPacket.WriteByte(0xFF)
	}
	for i := 0; i < 16; i++ {
		magicPacket.Write(mac)
	}

	// Resolve broadcast IP
