package main

import (
	"net"
	"log"
	"sync"
	"time"

	"golang.org/x/net/proxy"
)

type UDP struct {
	addr     string
	uaddr    *net.UDPAddr
	dialer   proxy.Dialer
	proxy    proxy.Proxy
	sessions sync.Map
}

type session struct {
	key      string
	src      *net.UDPAddr
	conn     net.PacketConn
	msgCh    chan []byte
	finCh    chan struct{}
}

type PktConn struct {
	net.PacketConn
	uaddr *net.UDPAddr
}

func (p *PktConn) WriteTo(b []byte, addr net.Addr) (int, error) {
	return p.PacketConn.WriteTo(b, p.uaddr)
}

func NewUDP(s string, d proxy.Dialer, p proxy.Proxy) (*UDP, error) {
	u, err := net.ResolveUDPAddr("udp", s)
	if err != nil {
		return nil, err
	}
	return &UDP{
		addr:   s,
		uaddr:  u,
		dialer: d,
		proxy:  p,
	}, nil
}

func (u *UDP) ListenAndServe() {
	conn, err := net.ListenPacket("udp", u.addr)
	if err != nil {
		log.Fatal(err)
	}
	defer conn.Close()

	buf := make([]byte, 4096)
	for {
		n, src, err := conn.ReadFrom(buf)
		if err != nil {
			log.Println("Error reading:", err)
			continue
		}

		sessionKey := src.String()
		if _, exists := u.sessions.Load(sessionKey); !exists {
			session := &session{
				key:     sessionKey,
				src:     src,
				conn:    conn,
				msgCh:   make(chan []byte),
				finCh:   make(chan struct{}),
			}
			u.sessions.Store(sessionKey, session)
			go u.serveSession(session)
		}

		session, _ := u.sessions.Load(sessionKey)
		session.(*session).msgCh <- buf[:n]
	}
}

func (u *UDP) serveSession(session *session) {
	defer func() {
		u.sessions.Delete(session.key)
		session.conn.Close()
		close(session.msgCh)
		close(session.finCh)
	}()

	proxyConn, err := u.DialUDP("udp", "127.0.0.1:8080")
	if err != nil {
		log.Println("Error dialing proxy:", err)
		return
	}
	defer proxyConn.Close()

	for {
		select {
		case msg, ok := <-session.msgCh:
			if !ok {
				return
			}
			_, err := proxyConn.WriteTo(msg, session.src)
			if err != nil {
				log.Println("Error writing to proxy:", err)
				return
			}
		case <-session.finCh:
			return
		}
	}
}

func (u *UDP) DialUDP(network, addr string) (net.PacketConn, error) {
	conn, err := u.dialer.Dial(network, addr)
	if err != nil {
		return nil, err
	}
	return &PktConn{conn, u.uaddr}, nil
}

func main() {
	dialer, err := proxy.SOCKS5("tcp", "127.0.0.1:1080", nil, proxy.Direct)
	if err != nil {
		log.Fatal(err)
	}
	proxyImpl := proxy.NewProxy(dialer)

	udpServer, err := NewUDP(":12345", dialer, proxyImpl)
	if err != nil {
		log.Fatal(err)
	}

	udpServer.ListenAndServe()
}
