		}
	}()

	err = cmd.Run()
	if err != nil {
		return fmt.Errorf("failed to run osascript: %w", err)
	}

	// Clear clipboard after use
	_, err = exec.CommandContext(ctx, "osascript", "-e", "set the clipboard to \"\"").Output()
	if err != nil {
		return fmt.Errorf("failed to clear clipboard: %w", err)
	}

	return nil
}
