package main

import (
	"bytes"
	"context"
	"errors"
	"fmt"
	"os"
	"os/exec"
	"time"
)

type proxyCommand struct {
	cmd *exec.Cmd
}

func (p *proxyCommand) Start() error {
	errPipeReader, errPipeWriter := os.Pipe()
	if err := p.cmd.Start(); err != nil {
		return fmt.Errorf("failed to start command: %w", err)
	}

	defer errPipeReader.Close()
	defer errPipeWriter.Close()

	ctx, cancel := context.WithTimeout(context.Background(), 16*time.Second)
	defer cancel()

	var buf bytes.Buffer
	go func() {
		p.cmd.Stdout = &buf
		p.cmd.Stderr = errPipeWriter
		p.cmd.Wait()
	}()

	select {
	case <-ctx.Done():
		if ctx.Err() == context.DeadlineExceeded {
			return errors.New("command timed out")
		}
		return ctx.Err()
	case <-errPipeReader.Ctx.Done():
		if errPipeReader.Ctx.Err() == context.Canceled {
			return errors.New("command failed to start")
		}
		return errPipeReader.Ctx.Err()
	}
}

