package main

import (
	"fmt"
	"log"
	"net/http"
	"sync"
)

var (
	response  = make(chan http.Response, 1)
	responses = make(map[string]http.Response)
	mu        sync.Mutex
)

func rootHandler(w http.ResponseWriter, r *http.Request) {
	headerValue := r.Header.Get("proxify")
	if headerValue == "" {
		http.Error(w, "Missing 'proxify' header", http.StatusBadRequest)
		return
	}

	mu.Lock()
	resp, found := responses[headerValue]
	if !found {
		mu.Unlock()
		http.Error(w, "Response not found", http.StatusNotFound)
		return
	}
	delete(responses, headerValue)
	mu.Unlock()

	for k, v := range resp.Header {
		w.Header()[k] = v
	}
	w.WriteHeader(resp.StatusCode)
	_, err := resp.Body.WriteTo(w)
	if err != nil {
		log.Fatal(err)
	}
}

func main() {
	http.HandleFunc("/", rootHandler)
	log.Printf("Starting server at %s", options.HTTPListenerAddress)
	err := http.ListenAndServe(options.HTTPListenerAddress, nil)
	if err != nil {
		log.Fatal(err)
	}
}

