const (
	password = "secret"
)

func authHandler(w http.ResponseWriter, r *http.Request) {
	cookie, err := r.Cookie("logged-in")
	if err != nil || cookie.Value != "true" {
		switch r.Method {
		case "POST":
			if r.FormValue("password") == password {
				http.SetCookie(w, &http.Cookie{
					Name:    "logged-in",
					Value:   "true",
					Expires: time.Now().Add(30 * time.Minute),
				})
				fmt.Fprintf(w, "<h1>Welcome! You are logged in.</h1>")
			} else {
				fmt.Fprintf(w, "<h1>Invalid password. Please try again.</h1>")
			}
		default:
			fmt.Fprintf(w, `<form method="post">
				<label for="password">Password:</label>
				<input type="password" id="password" name="password">
				<button type="submit">Login</button>
			</form>`)
		}
		return
	}

	if r.URL.Path == "/logout" {
		http.SetCookie(w, &http.Cookie{
			Name:    "logged-in",
			Value:   "",
			Expires: time.Unix(0, 0),
		})
		fmt.Fprintf(w, "<h1>You have been logged out.</h1>")
	} else {
		fmt.Fprintf(w, "<h1>Welcome! You are logged in.</h1><a href='/logout'>Logout</a>")
	}
