package main

import (
	"html/template"
	"log"
	"net/http"
	"path/filepath"
)

type Example struct {
	Type  string
	Link  string
	Other string
}

func getExamples() []Example {
	return []Example{
		{"Type1", "Link1", "Other1"},
		{"Type2", "Link2", "Other2"},
	}
}

func serve(addr string) error {
	examples := getExamples()

	tmpl := template.Must(template.ParseFiles("templates/home.html", "templates/example.html"))

	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		if err := tmpl.ExecuteTemplate(w, "home.html", examples); err != nil {
			log.Printf("Error executing template: %v", err)
		}
	})

	http.HandleFunc("/wasm_exec.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeFile(w, r, "static/wasm_exec.js")
	})

	http.HandleFunc("/example/", func(w http.ResponseWriter, r *http.Request) {
		path := r.URL.Path[len("/example/"):]
		for _, ex := range examples {
			if path == ex.Type+"/"+ex.Link {
				data := map[string]string{
					"Type":  ex.Type,
					"Link":  ex.Link,
					"Other": ex.Other,
				}
				if err := tmpl.ExecuteTemplate(w, "example.html", data); err != nil {
					log.Printf("Error executing template: %v", err)
				}
				return
			}
		}
		http.NotFound(w, r)
	})

	http.Handle("/static/", http.StripPrefix("/static/", http.FileServer(http.Dir("static"))))

	return http.ListenAndServe(addr, nil)
}
