package main

import (
	"context"
	"fmt"
	"net/http"
	"strings"

	"golang.org/x/sync/errgroup"
	"github.com/miekg/dns"
)

type localNetworkServices struct {
	domain   string
	webpages map[string]string
	ipAddr   string
}

func (s *localNetworkServices) DNSServerIP() string {
	return s.ipAddr
}

func (s *localNetworkServices) URL(subpath string) string {
	return fmt.Sprintf("http://%s%s", s.domain, subpath)
}

func (s *localNetworkServices) Serve(ctx context.Context) error {
	g, ctx := errgroup.WithContext(ctx)

	// Start HTTP server
	g.Go(func() error {
		http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
			path := strings.TrimPrefix(r.URL.Path, "/")
			if content, exists := s.webpages[path]; exists {
				fmt.Fprint(w, content)
			} else {
				http.NotFound(w, r)
			}
		})
		return http.ListenAndServe(s.ipAddr+":80", nil)
	})

	// Start DNS server
	g.Go(func() error {
		server := &dns.Server{Addr: s.ipAddr+":53", Net: "udp"}
		server.Handler = &dnsHandler{records: map[string]string{s.domain: s.ipAddr}}
		return server.ListenAndServe()
	})

	return g.Wait()
}

type dnsHandler struct {
	records map[string]string
}

func (h *dnsHandler) ServeDNS(w dns.ResponseWriter, r *dns.Msg) {
	m := new(dns.Msg)
	m.SetReply(r)
	m.Compress = false

	switch r.Question[0].Qtype {
	case dns.TypeA:
		for _, q := range r.Question {
			if ip, ok := h.records[q.Name]; ok {
				rr, _ := dns.NewRR(fmt.Sprintf("%s. 3600 IN A %s", q.Name, ip))
				m.Answer = append(m.Answer, rr)
			}
		}
	}

	w.WriteMsg(m)
}
