package main

import (
	"context"
	"fmt"
	"log"
	"net/http"

	"go.opencensus.io/plugin/ochttp"
	"go.opencensus.io/trace"
)

func main() {
	// Initialize OpenCensus with a default exporter
	exporter, err := trace.NewExportPipeline(trace.ExporterOptions{
		ServiceName: "my-service",
	})
	if err != nil {
		log.Fatalf("Failed to initialize OpenCensus exporter: %v", err)
	}
	defer exporter.Flush()

	// Create a new HTTP client with OpenCensus instrumentation
	client := &http.Client{Transport: &ochttp.Transport{}}

	// Define the root handler
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		ctx := r.Context()

		// Start a new span
		ctx, span := trace.StartSpan(ctx, "handle-root")
		defer span.End()

		// Add attributes to the span
		span.AddAttributes(
			trace.StringAttribute("http.method", r.Method),
			trace.StringAttribute("http.url", r.URL.Path),
		)

		// Respond with a simple message
		fmt.Fprintf(w, "hello world")

		// Make an external HTTP request
		_, err := client.Get("http://example.com")
		if err != nil {
			span.SetStatus(trace.Status{Code: trace.StatusCodeUnknown, Message: err.Error()})
		}
	})

	// Start the HTTP server
	log.Fatal(http.ListenAndServe(":50030", &ochttp.Handler{}))
}

