			}
			return false, nil
		},
	})

	// Routes
	e.POST("/upload", uploadHandler).Use(basicAuthMiddleware)
	e.POST("/install", installHandler).Use(keyAuthMiddleware)
	e.PUT("/rename", renameHandler).Use(basicAuthMiddleware)
	e.DELETE("/delete", deleteHandler).Use(keyAuthMiddleware)

	// Start server
