package bridge

import (
	"net/http"
	"sync"

	"github.com/igm/sockjs-go/v3/sockjs"
	"github.com/jpillora/ring"
	"github.com/labstack/echo/v4"
	"github.com/olahol/melody"
)

type API struct {
	Messages *ring.Ring
	RWMutex  sync.RWMutex
	Config   *Config
	mrouter  *melody.Melody
}

type Bridger interface {
	Start() error
}

type Config struct {
	Address     string
	Port        int
	BufferSize  int
	AuthEnabled bool
}

func New(cfg *Config) Bridger {
	api := &API{
		Messages: ring.New(cfg.BufferSize),
		Config:   cfg,
		mrouter:  melody.New(),
	}
	api.setupRoutes()
	return api
}

func (a *API) setupRoutes() {
	e := echo.New()
	e.GET("/ws", a.handleWebsocket)
	e.GET("/health", a.handleHealthcheck)
	e.GET("/messages", a.handleMessages)
	e.GET("/stream", a.handleStream)
	e.POST("/message", a.handlePostMessage)
	go func() {
		if err := e.Start(a.Config.Address + ":" + string(a.Config.Port)); err != nil {
			panic(err)
		}
	}()
}

func (a *API) handleWebsocketMessage(message Message, s *melody.Session) {
	a.RWMutex.Lock()
	defer a.RWMutex.Unlock()
	a.Messages.Value = append(a.Messages.Value.([]Message), message)
	a.mrouter.BroadcastFilter(func(q *melody.Session) bool {
		return q != s
	}, message)
}

func (a *API) handleWebsocket(c echo.Context) error {
	a.mrouter.HandleRequest(c.Response(), c.Request())
	return nil
}

func (a *API) handleHealthcheck(c echo.Context) error {
	return c.JSON(http.StatusOK, map[string]string{"status": "ok"})
}

func (a *API) handleMessages(c echo.Context) error {
	a.RWMutex.RLock()
	defer a.RWMutex.RUnlock()
	return c.JSON(http.StatusOK, a.Messages.Value)
}

func (a *API) handleStream(c echo.Context) error {
	sess, err := sockjs.NewSession(c.Response(), c.Request())
	if err != nil {
		return err
	}
	for _, msg := range a.Messages.Value.([]Message) {
		if err := sess.Send(msg); err != nil {
			return err
		}
	}
	return nil
}

func (a *API) handlePostMessage(c echo.Context) error {
	var msg Message
	if err := c.Bind(&msg); err != nil {
		return err
	}
	a.handleWebsocketMessage(msg, nil)
	return c.JSON(http.StatusCreated, msg)
}

type Message struct {
	Text string `json:"text"`
}

