	"github.com/alibaba/sentinel-golang/logging"
	"github.com/alibaba/sentinel-golang/util"
)

func init() {
	err := flow.LoadRules([]*flow.Rule{
		{
			Resource:               "test",
			TokenCalculateStrategy: flow.Direct,
			ControlBehavior:        flow.Reject,
			Threshold:              10,
			StatIntervalInMs:       1000,
		},
	})
	if err != nil {
		logging.Error(err, "Unexpected error while loading flow control rules")
		panic(err)
	}
}

func realIPFromHeader(ctx *fiber.Ctx) string {
	ip := ctx.Get("X-Forwarded-For")
	if ip == "" {
		ip = ctx.Get("X-Real-IP")
	}
	if ip == "" {
		ip = ctx.IP()
	}
	return ip
}

func blockFallback(ctx *fiber.Ctx) error {
	resp := map[string]string{"message": "Request blocked by rate limiting"}
	ctx.Set(fiber.HeaderContentType, fiber.MIMEApplicationJSONCharsetUTF8)
	ctx.Status(http.StatusTooManyRequests)
	return json.NewEncoder(ctx.Response().BodyWriter()).Encode(resp)
}

func main() {
	app := fiber.New()

	app.Use(func(ctx *fiber.Ctx) error {
		ip := realIPFromHeader(ctx)
		e, b := flow.Entry(ip, flow.WithTrafficType(flow.Inbound), flow.WithResource(ctx.Path()))
		if b != nil {
			return blockFallback(ctx)
		}
		defer e.Exit()
		return ctx.Next()
	})

	app.Get("/test", func(ctx *fiber.Ctx) error {
		return ctx.JSON(fiber.Map{"message": "Hello, World!"})
	})

	fmt.Println("Server is running on port 8080")
	if err := app.Listen(":8080"); err != nil {
		panic(err)
	}
}

