package main

import (
	"fmt"
	"io"
	"log"
	"os"
	"path/filepath"

	"github.com/gin-gonic/gin"
)

func setupServer() {
	r := gin.Default()

	// Serve static files from the ./public directory
	r.Static("/", "./public")

	// Setup POST endpoint for file uploads
	r.POST("/upload", func(c *gin.Context) {
		name := c.PostForm("name")
		email := c.PostForm("email")

		if name == "" || email == "" {
			c.JSON(400, gin.H{"error": "Name and email are required"})
			return
		}

		// Create a directory for uploaded files if it doesn't exist
		uploadDir := "./uploads"
		if err := os.MkdirAll(uploadDir, os.ModePerm); err != nil {
			c.JSON(400, gin.H{"error": fmt.Sprintf("Failed to create upload directory: %v", err)})
			return
		}

		// Retrieve the uploaded files from form data
		form, err := c.MultipartForm()
		if err != nil {
			c.JSON(400, gin.H{"error": fmt.Sprintf("Failed to parse multipart form: %v", err)})
			return
		}
		files := form.File["files"]

		for _, file := range files {
			dst := filepath.Join(uploadDir, file.Filename)

			// Save the uploaded file to the server
			if err := saveFile(file, dst); err != nil {
				c.JSON(400, gin.H{"error": fmt.Sprintf("Failed to save file: %v", err)})
				return
			}
		}

		c.JSON(200, gin.H{"message": "Files uploaded successfully"})
	})

	// Start the server
	if err := r.Run(":8080"); err != nil {
		log.Fatalf("Failed to start server: %v", err)
	}
}

func saveFile(file *multipart.FileHeader, dst string) error {
	src, err := file.Open()
	if err != nil {
		return err
	}
	defer src.Close()

	out, err := os.Create(dst)
	if err != nil {
		return err
	}
	defer out.Close()

	_, err = io.Copy(out, src)
	return err
}

func main() {
	setupServer()
}

