package main

import (
	"fmt"
	"net/http"
	"time"

	"github.com/gin-gonic/gin"
)

type StreamServer struct {
	router     *gin.Engine
	port       string
	streamData chan string
}

func NewStreamServer(port string) *StreamServer {
	return &StreamServer{
		router:     gin.Default(),
		port:       port,
		streamData: make(chan string),
	}
}

func (s *StreamServer) SetupRoutes() {
	s.router.GET("/stream", func(c *gin.Context) {
		c.Header("Content-Type", "text/event-stream")
		c.Header("Cache-Control", "no-cache")
		c.Header("Connection", "keep-alive")

		flusher, ok := c.Writer.(http.Flusher)
		if !ok {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Streaming unsupported!"})
			return
		}

		for {
			select {
			case message, open := <-s.streamData:
				if !open {
					return
				}
				c.SSEvent("message", message)
				flusher.Flush()
			}
		}
	})
}

func (s *StreamServer) GenerateStreamData() {
	go func() {
		for i := 0; ; i++ {
			time.Sleep(2 * time.Second)
			s.streamData <- fmt.Sprintf("Data packet %d", i)
		}
	}()
}

func (s *StreamServer) Start() error {
	return s.router.Run(":" + s.port)
}

func (s *StreamServer) Stop() {
	close(s.streamData)
}

func main() {
	server := NewStreamServer("8080")
	server.SetupRoutes()
	server.GenerateStreamData()
	if err := server.Start(); err != nil {
		panic(err)
	}
}

